import React, { Component } from "react";

export default class navBar extends Component {
    constructor(){
        this.state = {
            mode:'main'
        }
    }
  render() {
    function loadMain() {
      //e.preventDefault();
      console.log("You clicked for the Main Page.");
      this.state = {
          mode:'main'
      }
    }
    function loadCustom() {
      //e.preventDefault();
      console.log("You clicked for the Custom Page.");
      this.state = {
          mode:'custom'
      }
    }
    function loadAbout() {
      //e.preventDefault();
      console.log("You clicked for the About Page.");
      this.state = {
          mode:'about'
      }
    }
    return (
      <nav className="navbar fixed-top navbar-dark bg-dark">
        <div className="container-fluid justify-content-center">
          <ul className="nav list-inline">
            <li className="btn rounded list-inline-item">
              <a href="#" style={{ color: "linen" }} onClick={loadMain}>
                Official Fantasy
              </a>
            </li>
            <li
              className="btn rounded list-inline-item"
              onClick={loadCustom}
            >
              <a href="#" style={{ color: "linen" }}>
                Custom Fantasy
              </a>
            </li>
            <li
              className="btn rounded list-inline-item"
              onClick={loadAbout}
            >
              <a href="#" style={{ color: "linen" }}>
                About
              </a>
            </li>
          </ul>
        </div>
      </nav>
    );
  }
}
