import React, { Component } from 'react'

export default class checkbox extends Component {
    constructor(props){
        super(props);
        this.state = {
            id:this.props.id,
            selected:false
        }
        this.props.getState= function(){
            if (this.state.selected){
                return 1;
            } else {
                return 0;
            }
        };
    }
  render() {
    return (
        <div className="checkbox">
        <label>
          <input type="checkbox" value="" onClick={this.state.selected = !this.state.selected}/>
            {this.props.text}
        </label>
      </div>
    )
  }
}
